//faq
function faqAccordion($this){
	$this.classList.toggle('open');
	console.log($this);
	$this.nextElementSibling.classList.toggle('hide');
}
var faqTexts = document.querySelectorAll('.how-it-work__txt');
for (let i = 0; i < faqTexts.length; i++) {	
	var thisHeight = faqTexts[i].getBoundingClientRect().height;
	faqTexts[i].style.height = (thisHeight + 'px');
	faqTexts[i].classList.add('hide');
	function ready(){faqTexts[i].classList.add('ready');}
	setTimeout(ready, 250);
}

//timer
if(document.getElementById('js-timer')){
	function getTimeRemaining(endtime) {
	  var t = Date.parse(endtime) - Date.parse(new Date());
	  var seconds = Math.floor((t / 1000) % 60);
	  var minutes = Math.floor((t / 1000 / 60) % 60);
//	  var hours = Math.floor((t / (1000 * 60 * 60)) % 24);
//	  var days = Math.floor(t / (1000 * 60 * 60 * 24));
	  return {
		'total': t,
//		'days': days,
//		'hours': hours,
		'minutes': minutes,
		'seconds': seconds
	  };
	}

	function initializeClock(id, endtime) {
	  var clock = document.getElementById(id);
//	  var daysSpan = clock.querySelector('.days');
//	  var hoursSpan = clock.querySelector('.hours');
	  var minutesSpan = clock.querySelector('.minutes');
	  var secondsSpan = clock.querySelector('.seconds');

	  function updateClock() {
		var t = getTimeRemaining(endtime);

//		daysSpan.innerHTML = t.days;
//		hoursSpan.innerHTML = ('0' + t.hours).slice(-2);
		minutesSpan.innerHTML = ('0' + t.minutes).slice(-2);
		secondsSpan.innerHTML = ('0' + t.seconds).slice(-2);

		if (t.total <= 0) {
		  clearInterval(timeinterval);
		}
	  }

	  updateClock();
	  var timeinterval = setInterval(updateClock, 1000);
	}

	//var deadline="October 29 2019 15:00:00 GMT+0300";
	var deadline = new Date(Date.parse(new Date()) + 180000); // for endless timer
	initializeClock('js-timer', deadline);
}

//log
if(document.getElementById('js-log')){
  function toogleClassFnc(elem, duration){

    function toggleClass(){
      elem.classList.add('active');
      elem.previousElementSibling.classList.remove('active');
    }
    setTimeout(toggleClass, duration);
  }
  toogleClassFnc(document.querySelectorAll('.log__item')[1], 30000);
  toogleClassFnc(document.querySelectorAll('.log__item')[2], 60000);
  toogleClassFnc(document.querySelectorAll('.log__item')[3], 90000);
  toogleClassFnc(document.querySelectorAll('.log__item')[4], 120000);
  toogleClassFnc(document.querySelectorAll('.log__item')[5], 150000);
}

//styled selects
var x, i, j, selElmnt, a, b, c;
x = document.getElementsByClassName("js-styled-select");
for (i = 0; i < x.length; i++) {
  selElmnt = x[i].getElementsByTagName("select")[0];
  a = document.createElement("DIV");
  a.setAttribute("class", "select-selected");
  a.innerHTML = selElmnt.options[selElmnt.selectedIndex].innerHTML;
  x[i].appendChild(a);
  b = document.createElement("DIV");
  b.setAttribute("class", "select-items select-hide");
  for (j = 1; j < selElmnt.length; j++) {
	c = document.createElement("DIV");
	c.innerHTML = selElmnt.options[j].innerHTML;
	c.addEventListener("click", function(e) {
		var y, i, k, s, h;
		s = this.parentNode.parentNode.getElementsByTagName("select")[0];
		h = this.parentNode.previousSibling;
		for (i = 0; i < s.length; i++) {
		  if (s.options[i].innerHTML == this.innerHTML) {
			s.selectedIndex = i;
			h.innerHTML = this.innerHTML;
			y = this.parentNode.getElementsByClassName("same-as-selected");
			for (k = 0; k < y.length; k++) {
			  y[k].removeAttribute("class");
			}
			this.setAttribute("class", "same-as-selected");
			break;
		  }
		}
        
        
		h.click();
        //смена картинки темы
        if(document.getElementById('js-scin-pic-select')){
          document.getElementById('js-skin-pic').setAttribute('src', 'img/pic/'+ s.value + '.png')
        }
	});
	b.appendChild(c);
  }
  x[i].appendChild(b);
  a.addEventListener("click", function(e) {
	e.stopPropagation();
	closeAllSelect(this);
	this.nextSibling.classList.toggle("select-hide");
	this.classList.toggle("select-arrow-active");
  });
}

function closeAllSelect(elmnt) {
  var x, y, i, arrNo = [];
  x = document.getElementsByClassName("select-items");
  y = document.getElementsByClassName("select-selected");
  for (i = 0; i < y.length; i++) {
	if (elmnt == y[i]) {
	  arrNo.push(i)
	} else {
	  y[i].classList.remove("select-arrow-active");
	}
  }
  for (i = 0; i < x.length; i++) {
	if (arrNo.indexOf(i)) {
	  x[i].classList.add("select-hide");
	}
  }
}
document.addEventListener("click", closeAllSelect);
